function loadUser(){$.get(serverContext+"/user/data",function(o){o?($(".user").show(),$(".userName").html(o.firstName)):$(".nouser").show()}).fail(function(o){$(".nouser").show()})}var serverContext=window.location.protocol+"//"+window.location.host+"/";$(document).ready(function(){$(".user").hide(),$(".nouser").hide(),loadUser()});