function loadUser(){$.get(serverContext+"/user/data",function(o){o?($(".user").show(),$(".nouser").hide(),$(".userName").html(o.firstName)):($(".nouser").show(),$(".user").hide())}).fail(function(o){$(".nouser").show(),$(".user").hide()})}function logout(){$.get(serverContext+"/csrf",function(o){$.ajaxSetup({headers:{"X-CSRF-TOKEN":o.token}}),$.post(serverContext+"/logout",{},function(o){loadUser()}).fail(function(o){console.log("Logout Failed")})}).fail(function(o){console.log("CSRF Failure")})}var serverContext=window.location.protocol+"//"+window.location.host+"/";$(document).ready(function(){$(".user").hide(),$(".nouser").hide(),loadUser()});